import UIKit

protocol ListMoreBusinessLogic {
  func fetchSettings(request: ListMore.Fetch.Request)
}

protocol ListMoreDataStore {
  var settings: [Setting]? { get }
}

class ListMoreInteractor: ListMoreBusinessLogic, ListMoreDataStore {
  var presenter: ListMorePresentationLogic?
  var worker: ListMoreWorker?

  var settings: [Setting]?

  // MARK: Fetch settings

  func fetchSettings(request: ListMore.Fetch.Request) {
    self.settings = APPSetting.settings
    let response = ListMore.Fetch.Response(settings: settings)
    presenter?.presentFetchedSettings(response: response)
  }
}
